function popUp(){const t=document.getElementById("overlay"),e=document.querySelector(".btn"),n=document.getElementsByClassName("close")[0];e.onclick=function(){t.style.display="block"},n.onclick=function(){t.style.display="none"},window.onclick=function(e){e.target==t&&(t.style.display="none")}}popUp();const btn=document.querySelector(".btn"),infoClick=document.querySelector(".info-click"),el=document.createElement("span");let counter=0;function elementClick(){if(counter+=1,infoClick.innerText=counter+" times",localStorage.setItem("counter",JSON.stringify(counter)),5==counter){btn.removeEventListener("click",elementClick),el.classList.add("element"),el.innerText="Reset Now!";const e=document.querySelector(".btn-reset");e.appendChild(el)}}window.onload=()=>{counter=localStorage.getItem("counter")?JSON.parse(localStorage.getItem("counter")):0,infoClick.innerText=counter},btn.addEventListener("click",elementClick);const btnReset=document.querySelector(".btn-reset");function localStorageClickClear(){infoClick.innerText="0 times",window.location.reload(localStorage.clear("counter"))}btnReset.addEventListener("click",localStorageClickClear);